// Matrix Assignment Operations and Exceptions
TEST(MatrixAssignment, SquareMatrix) {
	std::vector<std::vector<double>> myVect{
		{1, 2, 3},
		{0, 5, 6},
		{0, 0, 9}
	};

	Matrix myMatrix(3, 3);
	for (int i = 0; i < 3; i++) {
		for (int j = 0; j < 3; j++) {
			myMatrix[i][j] = myVect[i][j];
		}
	}

	EXPECT_EQ(Matrix(myVect), myMatrix);
}

