TEST(KMeansClustering, Cluster1) {
	std::vector<std::vector<double>> testVect1{
		{2, 0, 0},
		{1, 1, 0},
		{2, 1, 0},
		{0, 4, 5},
		{0, 4, 3},
		{0, 4, 4},
		{0, 4, 6}
	};
	std::vector<double> testVect2{
		0, 0, 0, 1, 1, 1, 1
	};
	EXPECT_EQ(Matrix(testVect1).KMeans(2), Vector(testVect2));
}
